import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedContainer, setSelectedContainer] = useState(''); // Selected container name
  const [results, setResults] = useState(null); // API results

  // Function to fetch data for the selected container
  const fetchContainerData = async () => {
    if (!selectedContainer) {
      alert('Please select a container first.');
      return;
    }

    try {
      const response = await axios.post(
        `https://idr-testing.openmicroscopy.org/searchengine2/api/v1/resources/sub_container_images/`,
        null,
        {
          params: { container_name: selectedContainer },
          headers: {
            accept: 'application/json',
            'Content-Type': 'application/json',
          },
        }
      );
      setResults(response.data); // Assume the response is JSON data
    } catch (error) {
      console.error('Error fetching container data:', error);
      alert('Failed to fetch data. Please try again.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Container Data Viewer</h1>

      {/* Dropdown for container names */}
      <div style={{ marginBottom: '20px' }}>
        <label htmlFor="container-select">Select a Container:</label>
        <select
          id="container-select"
          value={selectedContainer}
          onChange={(e) => setSelectedContainer(e.target.value)}
          style={{ marginLeft: '10px', padding: '5px' }}
        >
          <option value="">-- Select a container --</option>
          <option value="idr">idr</option>
          <option value="ssbd">ssbd</option>
          <option value="bia">bia</option>
        </select>
      </div>

      {/* Button to fetch data */}
      <div style={{ marginBottom: '20px' }}>
        <button
          onClick={fetchContainerData}
          style={{ padding: '10px 20px', backgroundColor: '#007BFF', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}
        >
          Fetch Data
        </button>
      </div>

      {/* Display results */}
      <div>
        <h2>Results:</h2>
        {results ? (
          <pre style={{ background: '#f4f4f4', padding: '10px', borderRadius: '5px' }}>
            {JSON.stringify(results, null, 2)}
          </pre>
        ) : (
          <p>No data to display. Click "Fetch Data" after selecting a container.</p>
        )}
      </div>
    </div>
  );
};

export default App;
